// Format WordPress data for React frontend
return {
  posts: items.map(item => {
    const post = item.json;
    return {
      id: post.id,
      title: post.title.rendered,
      slug: post.slug,
      content: post.content.rendered,
      excerpt: post.excerpt.rendered,
      featuredImage: post._embedded && 
                    post._embedded['wp:featuredmedia']? 
                    post._embedded['wp:featuredmedia'][0].source_url : null,
      author: post._embedded && 
              post._embedded.author? 
              post._embedded.author[0].name : null,
      date: post.date,
      modified: post.modified,
      categories: post._embedded && 
                post._embedded['wp:term'] ? 
                post._embedded['wp:term'][0].map(cat => ({
                  id: cat.id,
                  name: cat.name,
                  slug: cat.slug
                })) : []
    };
  }),
  totalPages: items.length > 0 ? parseInt(items[0].headers['x-wp-totalpages']) || 1 : 1,
  total: items.length > 0 ? parseInt(items[0].headers['x-wp-total']) || 0 : 0
};

// Get pagination headers from the response
const headers = items[0]?.headers || {};

// Create formatted post data for React frontend
return {
  posts: items.map(item => {
    const post = item.json;
    return {
      id: post.id,
      title: post.title.rendered,
      slug: post.slug,
      excerpt: post.excerpt.rendered,
      featuredImage: post._embedded && 
                    post._embedded['wp:featuredmedia']? 
                    post._embedded['wp:featuredmedia'][0].source_url : null,
      date: post.date,
      modified: post.modified,
      categories: post._embedded && 
                post._embedded['wp:term'] ? 
                post._embedded['wp:term'][0].map(cat => ({
                  id: cat.id,
                  name: cat.name,
                  slug: cat.slug
                })) : []
    };
  }),
  totalPages: parseInt(headers['x-wp-totalpages'] || '1'),
  total: parseInt(headers['x-wp-total'] || '0'),
  currentPage: parseInt($request.query.page || '1')
};

// Format menu data for React
return items.map(item => {
  return {
    id: item.json.ID,
    name: item.json.name,
    slug: item.json.slug,
    items: item.json.items?.map(menuItem => ({
      id: menuItem.ID,
      title: menuItem.title,
      url: menuItem.url,
      parent: menuItem.menu_item_parent,
      order: menuItem.menu_order,
      target: menuItem.target || '_self',
      classes: menuItem.classes || [],
      submenu: []
    })) || []
  };
}).map(menu => {
  // Organize menu items into hierarchical structure
  const itemMap = {};
  menu.items.forEach(item => { itemMap[item.id] = item; });

  const hierarchicalItems = [];
  menu.items.forEach(item => {
    if (!item.parent || item.parent === '0') {
      hierarchicalItems.push(item);
    } else if (itemMap[item.parent]) {
      if (!itemMap[item.parent].submenu) {
        itemMap[item.parent].submenu = [];
      }
      itemMap[item.parent].submenu.push(item);
    }
  });

  // Sort by menu order
  hierarchicalItems.sort((a, b) => a.order - b.order);
  hierarchicalItems.forEach(item => {
    if (item.submenu && item.submenu.length > 0) {
      item.submenu.sort((a, b) => a.order - b.order);
    }
  });

  return {
    ...menu,
    items: hierarchicalItems
  };
});

