// Rate limiting implementation
const { name, email, message } = $input.body;
const secretHeader = $node.headers['x-api-secret'];
const ip = $json.ip || '0.0.0.0';
const cacheKeyPrefix = 'form_submit_rate_';

// Check API secret first
if (secretHeader !== 'YOUR_SHARED_SECRET_HERE') {
  return { valid: false, error: 'Unauthorized access', status: 403 };
}

// Validate required fields
if (!name || !email || !message) {
  return { valid: false, error: 'Missing required fields', status: 400 };
}

// Validate email format
if (!email.match(/^\w[-\.\+]*@([\w-]+\.)+[\w-]{2,}$/)) {
  return { valid: false, error: 'Invalid email format', status: 400 };
}

// Basic sanitization
const sanitizedData = {
  name: name.replace(/[<>]/g, ''),
  email: email.trim(),
  message: message.replace(/[<>]/g, ''),
  submission_date: new Date().toISOString(),
  ip_address: ip
};

return { valid: true, data: sanitizedData };

// Process the WordPress form submission response
const response = $node['HTTP Request Submit Form'].json;

// Check if the submission was successful
if (response && response.success) {
  return {
    success: true,
    message: 'Form submitted successfully',
    reference: response.reference || null
  };
} else {
  // Handle potential WordPress errors
  return {
    success: false,
    message: response.message || 'Form submission failed',
    errors: response.errors || []
  };
}

// Validate authorization for content sync
const authHeader = $node.headers.authorization;
const apiKey = $node.headers['x-api-key'];
const expectedApiKey = 'YOUR_CONTENT_SYNC_API_KEY';

// Check if the request has valid authorization
if (!authHeader && (!apiKey || apiKey !== expectedApiKey)) {
  return {
    valid: false,
    error: 'Unauthorized',
    status: 403
  };
}

// Prepare sync data
return {
  valid: true,
  siteUrl: $json.siteUrl,
  body: {
    post_id: $json.body.post_id,
    action: $json.body.action || 'update',
    content_type: $json.body.content_type || 'post'
  }
};

// Process content updates
